<?php

/**
* Запуск скрипта из консоли:
* php translate.php > farengate.htm
* 
* Полный путь к книге на английском
* $book = 'Fahrenheit_451.txt';
* 
* Полный путь к файлу в котором будет храниться словарь
* $dictionary_file = 'spell_book.dat';
* 
* Ключик от переводчика yandex/ Можно переделать функцию на любой другой сервис переводов.
* define('API_KEY', 'Тут ваш ключик от yandex api переводчика. https://tech.yandex.ru/keys/get/?service=trnsl');
* 
* Авторские права принадлежат Infarmer:
* https://github.com/infarmer
* Но вы можете использовать и изменять как хотите
*/

$book = 'Fahrenheit_451.txt';
$dictionary_file = 'dictionary.dat';
define('API_KEY', 'Тут ваш ключик от yandex api переводчика. https://tech.yandex.ru/keys/get/?service=trnsl');

$txtbook = file_get_contents($book);
$words_text = parse_words($txtbook);
$words = explode_words($words_text);
$translated_words = translate($words, $dictionary_file);
$out_html = print_book($txtbook, $translated_words);
echo $out_html;

function translateYandex($word)
{

	$url = 'https://translate.yandex.net/api/v1.5/tr.json/translate?';


	$data['key'] = API_KEY;
	$data['lang'] = 'en-ru';
	$data['text'] = $word;

	$url .= http_build_query($data);

    $ch = curl_init();

    curl_setopt($ch, CURLOPT_URL, $url);

    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 5);

    $http_response = curl_exec($ch);
    $http_code = (int) curl_getinfo($ch, CURLINFO_HTTP_CODE);
    curl_close($ch);

    if (200 != $http_code) {
    	echo "error: $http_code \n";
    	return false;
    }

    $response = json_decode($http_response, true);

    if (empty($response)){
    	echo "error: not json \n";
        return false;
    }

    if (200 != $response['code']) {
    	echo "error: {$response['code']} \n";
    	return false;
    }


    return $response['text'];
}

# --------------------------------------------------------------------------

function parse_words($text){

    $charset_arr = range('A','Z');
    $charset_arr = array_merge($charset_arr, range('a','z'));

    $str_len = mb_strlen($text, 'UTF-8');

    $exit_text = '';
    for ($i=0; $i < $str_len ; $i+=10000) {
        $sub_text=mb_substr($text, $i, 10000, 'UTF-8');
        $sub_text_len = mb_strlen($sub_text, 'UTF-8');

        for ($k=0; $k < $sub_text_len ; $k++) {
            $char=mb_substr($sub_text, $k, 1, 'UTF-8');
            if(in_array($char, $charset_arr)){
                $exit_text.=$char;  
            } else{
                $exit_text.=' ';
            }
        }
    }

    return $exit_text;
}

# --------------------------------------------------------------------------

function explode_words($txtbook){
    $txtbook = preg_replace('~\s\s+~u', ' ', $txtbook);
    $words = explode(' ', $txtbook);
    $words = array_unique($words);
    foreach ($words as $key => $value) {
        $words[$key] = mb_strtolower($value, 'UTF-8');
    }
    $words = array_unique($words);
    $words = array_values($words);

    foreach ($words as $key => $word) {
        if(strlen($word) < 2){
            unset($words[$key]);
        }
    }

    $words = array_values($words);
    return $words; 
}

# --------------------------------------------------------------------------

function translate($words, $dictionary_file){
    $dictionary = (string)@file_get_contents($dictionary_file);

    $translated_words = unserialize($dictionary);
    if (empty($translated_words)) {
        $translated_words = array();
    }

    foreach ($words as $word) {
        if (!isset($translated_words[$word])) {
            $one_word = translateYandex($word);
            // echo $word.' : '.implode(', ', $one_word).PHP_EOL;
            if ($one_word == false) {
                break;
            } else {
                $translated_words[$word] = $one_word;
            }
        }
    }

    $dictionary = serialize($translated_words);
    file_put_contents($dictionary_file, $dictionary);

    return $translated_words;
}

