function print_book($text, $dictionary){

    $text = explode("\n", $text);

    $linux_css = '
    body{
        padding:30px 20%;
        font-size: 14pt;
        font-family: "DejaVu Serif";
        line-height: 1.5em;
        background-color: #595959;
        letter-spacing: 1px;
    }
    i{
        font-style: normal;
        cursor:pointer;
    }
    i:hover{
        /* font-weight: bold; */
        color: #FFBDBD;
    }
    p{
        color:white;
    }
    ';


    $win_css = '
    body{
        padding:30px 20%;
        font-size: 14pt;
        font-family: "Courier New", Courier, monospace;
        line-height: 1.5em;
        background-color: #595959;
    }
    i{
        font-style: normal;
        cursor:pointer;
    }
    i:hover{
        font-weight: bold;
        color: #FFBDBD;
    }
    p{
        color:white;
    }
    ';


    $out = '
    <html>
    <head>
        <meta charset="UTF-8">
        <style>'.$win_css.'</style>
    </head>
    <body>
    ';

    $charset_arr = range('A','Z');
    $charset_arr = array_merge($charset_arr, range('a','z'));

    foreach ($text as $string) {
        $string = trim($string);
        $str_len = mb_strlen($string, 'UTF-8');

        $exit_text = '';
        $word='';
        for ($i=0; $i < $str_len ; $i++) {
            $char=mb_substr($string, $i, 1, 'UTF-8');
            if(in_array($char, $charset_arr)){
                $word.=$char;
            } else{
                if(!empty($word)){
                    $exit_text.='<i>'.$word.'</i>';
                }
                $word='';
                $exit_text.=$char;
            }
        }
        if(!empty($word)){
            $exit_text.='<i>'.$word.'</i>';
        }
        $exit_text = '<p>'.$exit_text.'</p>';
        $out.= $exit_text.PHP_EOL;
    }

    $out.= '<script type="text/javascript">/*! jQuery v1.12.3 | (c) jQuery Foundation | jquery.org/license */
    '.base64_decode(
        ''
    ).'</script>';

    $out.= '<script type="text/javascript">var dictionary = '.json_encode($dictionary).';</script>';
    $out.= '
    <script type="text/javascript">
    $(document).ready(function(){
        $("i").hover(
            function(){
                var word = $(this).text();
                word = word.toLowerCase();
                if (dictionary[word] !== undefined) {
                    $(this).attr("title", dictionary[word].join(", "));    
                }
            },function(){});
    });
    </script>
    ';
    $out.= '</body></html>';

    return $out;
}

